package ayds.dictionary.foxtrot.services;public class ServiceModule {    private static ServiceModule instance;    private YandexService service;    private ServiceModule() {        service = new YandexServiceImpl();    }    public static ServiceModule getInstance() {      if (instance == null) {        instance = new ServiceModule();        }        return instance;    }    public YandexService getRemoteSource() {      return service;    }}