package ayds.dictionary.foxtrot.services;public class YandexServiceModule {    private static YandexServiceModule instance;    private YandexService service;    private YandexServiceModule() {        service = new YandexServiceImpl();    }    public static YandexServiceModule getInstance() {      if (instance == null) {        instance = new YandexServiceModule();        }        return instance;    }    public YandexService getRemoteSource() {      return service;    }}